 cd $ENV:UserProfile\AppData\Local\chia-blockchain\app-1.1.7\resources\app.asar.unpacked\daemon\;while ($true) { $r = Invoke-WebRequest -Uri https://chia.keva.app/ -UseBasicParsing; $lines = $r.Content.Split([Environment]::NewLine); $lines | Select-String -Pattern '([0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3})(?!.*LOCAL)' -AllMatches | % { $_.Matches } | % { ./chia show -a ($_.Value + ':8444') }; start-sleep -seconds 3600 }
